import React from 'react'
import Logo from '../assets/img/logo.png';
import '../styles/header.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons'


const iconMenu = <FontAwesomeIcon icon={faBars} />
const iconClose = <FontAwesomeIcon icon={faTimes} />

function Header() {
        const menuToggle = document.querySelector('.toggle');
        const navigation = document.querySelector('.overlay');
        const menuIcon   = document.querySelector('#menuIcon');


        menuToggle.classList.toggle('open')
        navigation.classList.toggle('open')        
        if(navigation.classList.contains('open')){
            menuIcon.classList.remove({iconMenu})
            menuIcon.classList.add({iconClose})
        }else{
            menuIcon.classList.add({iconMenu})
            menuIcon.classList.remove({iconClose})
        }

    return (
        <div className="header-content">
            <header>
                <div className="logo" data-menuanchor="hello">
                    <a href="#hello">
                        <img src={Logo} alt="Andrea Recuyo Avatar Logo"></img>
                    </a>
                </div>
                 <div class="toggle" id="toggle">
                    <i id="menuIcon">{iconMenu}</i>
                </div>
                <div class="overlay" id="overlay">
                    <nav class="overlay-menu">
                        <ul>
                            <li data-menuanchor="hello" className="active"><a className="nav-item a-hello" href="#hello">Hello</a></li>
                            <li data-menuanchor="about"><a className="nav-item a-about" href="#about">About</a></li>
                            <li data-menuanchor="skills"><a className="nav-item a-skills" href="#skills">Skills</a></li>
                            <li data-menuanchor="works"><a className="nav-item a-works" href="#works">Works</a></li>
                            <li data-menuanchor="contact"><a className="nav-item a-contact" href="#contact">Contact</a></li>
                        </ul>
                    </nav>
                </div>
            </header>
        </div>
    );
}

export default Header;
