import React, { useState} from 'react'
import Logo from '../assets/img/logo.png';
import '../styles/header.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faBars, faTimes } from "@fortawesome/free-solid-svg-icons"

const iconMenu = <FontAwesomeIcon icon={faBars} />
const iconClose = <FontAwesomeIcon icon={faTimes} />


function toggleMenu(){
const [isOpen, setIsOpen] = useState(false);
        const toggle = () => setIsOpen(!isOpen);
        const hide = () => setIsOpen(false);
        const show = () => setIsOpen(true);

        const navigation = [
            { dataMenuanchor:"hello", link: '#hello', className:"a-hello", text: 'Hello' },
            { dataMenuanchor:"about", link: '#about', className:"a-about", text: 'About' },
            { dataMenuanchor:"skills", link: '#skills', className:"a-skills", text: 'Skills' },
            { dataMenuanchor:"works", link: '#works', className:"a-works", text: 'Works' },
            { dataMenuanchor:"contact", link: '#contact', className:"a-contact", text: 'Contact' },
        ];
}
function Header() {




    return (
        <div className="header-content">
            <header>
                <div className="logo" data-menuanchor="hello">
                    <a href="#hello">
                        <img src={Logo} alt="Andrea Recuyo Avatar Logo"></img>
                    </a>
                </div>
                <div className="toggle" onClick={toggle}>
                     <i className={iconMenu} id="menu"></i>
                    <i className={iconClose } id="close"></i>
                    {`menu ${isOpen ? '#menu': '#close'}`}

                </div>

                <ul className="nav">
                    {/* <li data-menuanchor="hello" className="active"><a className="a-hello" href="#hello">Hello</a></li>
                    <li data-menuanchor="about"><a className="a-about" href="#about">About</a></li>
                    <li data-menuanchor="skills"><a className="a-skills" href="#skills">Skills</a></li>
                    <li data-menuanchor="works"><a className="a-works" href="#works">Works</a></li>
                    <li data-menuanchor="contact"><a className="a-contact" href="#contact">Contact</a></li> */}

                    {navigation.map(nav => (
                    <li key={nav.text} data-menuanchor={nav.dataMenuanchor} >
                        <a className={ nav.className} href={nav.link} onClick={toggle} onBlur={hide} onFocus={show}>
                            {nav.text}
                        </a>
                    </li>
                    ))}
                </ul>
            </header>
        </div>
    );
}

export default Header;
