import React, {useState} from 'react'
import "../styles/tab.css";
import "../styles/works.css";
import WorkList, { Menu }from "./WorkList";
import {Carousel} from '3d-react-carousal';

    import imgCupcakes from '../assets/img/works/cupcakes-post.png';
import imgFbCover from '../assets/img/works/fb-cover.png';
import imgJustbnco from '../assets/img/works/justbnco.png';

function Works() {
    const [items, setItems] = useState(WorkList);

    const filterItem = (categItem) => {
        const updatedItems = WorkList.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }

const Menu = [
    {
        id: 1,
        image: imgCupcakes,
        name: "Cupcake Post",
        category: "designs",
        description: 'Social Media Advertising Template.',
        tag: ["Figma", " Adobe Illustrator"]
    },
    {
        id: 2,
        image: imgFbCover,
        name:"Facebook Covers",
        category: "designs",
        description: 'Facebook cover template.',
        tag: ["Adobe Photoshop"," Figma"]
    },
    {
        id: 3,
        image: imgJustbnco,
        name:"Just Bake & Co. Web Design",
        category: "designs",
        description: 'Website Design for Just Bake & Co. ',
        tag: ["Figma", " Wordpress", " Elementor"]
    }
];
    return (
        <div>
            <div className="works-content">
                <div className="works-header"><h1>Works</h1></div>
                <div className="menu-tabs">
                    <div className="menu-tab">
                        <button className="btn-dark" onClick={() => setItems(WorkList)}>All</button>
                        <button className="btn-dark" onClick={() => filterItem('designs')}>Designs</button>
                        <button className="btn-dark" onClick={() => filterItem('website')}>Website</button>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="menu-items">
                    <div className="row">
                        <div className="col-11 item">
                            <div className="row">
                                {
                                    items.map((elem) => {
                                        const { name, image, description, tag } = elem;

                                        return (
                                            <div>
                                                <Carousel slides={Menu} autoplay={true} interval={1000} />
                                                <div className="item">
                                                    <div className="item1 col-12">
                                                        <div className="row item-inside">
                                                            <div className="col-12 img-div">
                                                                <img src={image} alt={name} className="img" />
                                                                <div className="item-content">
                                                                    <h1>{name}</h1>
                                                                    <p>{description}</p>
                                                                    <div className="tag">
                                                                        <span className="badge-light">{tag}</span>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>


                                        )
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Works;
