import React from 'react'
import HeroAvatar from '../assets/img/avatar/ar-hero-avatar.png'
import '../styles/hero.css'

const hello = "<HelloWorld />";
const SmileyEmoji = "\uD83D\uDE00";
const WavingHandEmoji = "\uD83D\uDC4B";

function Hero() {

    return (
        <div className="container">
            <div className="hero">
                <div className="hero-text">
                    <code>{hello}</code>
                    <h1>I'm Andrea Recuyo</h1>
                    <h2>Front-End Developer {SmileyEmoji} </h2>
                    <button className="btn-dark" data-menuanchor="contact"><a className="a-contact" href="#contact">Say Hi {WavingHandEmoji}</a></button>
                    <button className="btn-transparent">Discover my works</button>
                </div>
                <div className="hero-avatar">
                    <img src={HeroAvatar} alt="Andrea Recuyo Avatar" className="avatar"></img>
                </div>
            </div>
        </div>
    )
}

export default Hero;
