import React, {useState} from 'react'
import "../styles/tab.css";
import MenuList from "./MenuList";


function Works() {
    const [items, setItems] = useState(MenuList);

    const filterItem = (categItem) => {
        const updatedItems = MenuList.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }
    return (
        <div className="container">
            <div className="works-header">
                <h1>Works</h1>
                <div className="menu-tabs">
                    <div className="menu-tab">
                        <button className="btn-dark" onClick={() => setItems(MenuList)}>All</button>
                        <button className="btn-dark" onClick={() => filterItem('designs')}>Designs</button>
                        <button className="btn-dark" onClick={() => filterItem('website')}>Website</button>
                    </div>
                </div>
            </div>
            <div className="menu-items">
                <div className="row">
                    <div className="col-11">
                        <div className="row">
                            {
                                items.map((elem) => {
                                    const { id, name, image, description } = elem;

                                    return (
                                        <div className="item1 col-12">
                                            <div className="row Item-inside">
                                                <div className="col-12 img-div">
                                                    <img src={image} alt={name} className="img-fluid"/>
                                                </div>
                                                <div className="col-12">
                                                    <div className="main-title">
                                                        <h1>{name}</h1>
                                                        <p>{description }</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Works;
