import React, {useState} from 'react'
import "../styles/tab.css";
import "../styles/works.css";
import WorkList from "./WorkList";
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/effect-coverflow/effect-coverflow.min.css"
import "swiper/components/pagination/pagination.min.css"
import SwiperCore, {
  EffectCoverflow,Pagination
} from 'swiper/core';

// install Swiper modules
SwiperCore.use([EffectCoverflow, Pagination]);


function Works() {
    const [items, setItems] = useState(WorkList);

    const filterItem = (categItem) => {
        const updatedItems = WorkList.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }
    return (
        <div>
            <div className="works-content">
                <div className="works-header">
                    <h1>Works</h1>
                </div>
                <div className="menu-tabs">
                    <div className="menu-tab">
                        <button className="btn-dark" onClick={() => setItems(WorkList)}>All</button>
                        <button className="btn-dark" onClick={() => filterItem('designs')}>Designs</button>
                        <button className="btn-dark" onClick={() => filterItem('website')}>Website</button>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="menu-items">
                    <div className="row">
                        <div className="col-11 item">
                            <div className="row">
                                {
                                    items.map((elem) => {
                                        const { name, image, description, tag } = elem;

                                        return (
                                            <Swiper effect={'coverflow'} grabCursor={true} centeredSlides={true} slidesPerView={'auto'} coverflowEffect={{
                                                "rotate": 50,
                                                "stretch": 0,
                                                "depth": 100,
                                                "modifier": 1,
                                                "slideShadows": true
                                                }} pagination={true} className="mySwiper">
                                                <SwiperSlide>
                                                    <div className="item">
                                                <div className="item1 col-12">
                                                    <div className="row item-inside">
                                                        <div className="col-12 img-div">
                                                            <img src={image} alt={name} className="img" />
                                                            <div className="item-content">
                                                                <h1>{name}</h1>
                                                                <p>{description}</p>
                                                                <div className="tag">
                                                                    <span className="badge-light">{tag}</span>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                                </SwiperSlide>
                                            </Swiper>

                                        )
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Works;
