import React, { useState} from 'react'
import Logo from '../assets/img/logo.png';
import '../styles/header.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faBars, faTimes } from "@fortawesome/free-solid-svg-icons"

const iconMenu = <FontAwesomeIcon icon={faBars} />
const iconClose = <FontAwesomeIcon icon={faTimes} />


function Header() {

    const Nav = () => {
        const [isOpen, setIsOpen] = useState(false);
        const toggle = () => setIsOpen(!isOpen);
        const hide = () => setIsOpen(false);
        const show = () => setIsOpen(true);
    }

    return (
        <div className="header-content">
            <header>
                <div className="logo" data-menuanchor="hello">
                    <a href="#hello">
                        <img src={Logo} alt="Andrea Recuyo Avatar Logo"></img>
                    </a>
                </div>
                <div className="toggle" >
                    <i id="menuIcon">{iconMenu}</i>
                </div>

                <ul className="nav">
                    <li data-menuanchor="hello" className="active"><a className="a-hello" href="#hello">Hello</a></li>
                    <li data-menuanchor="about"><a className="a-about" href="#about">About</a></li>
                    <li data-menuanchor="skills"><a className="a-skills" href="#skills">Skills</a></li>
                    <li data-menuanchor="works"><a className="a-works" href="#works">Works</a></li>
                    <li data-menuanchor="contact"><a className="a-contact" href="#contact">Contact</a></li>
                </ul>
            </header>
        </div>
    );
}

export default Header;
