import React, {useState} from 'react'
import "../styles/tab.css";
import "../styles/works.css";
import WorkList from "./WorkList";


function Works() {
    const [items, setItems] = useState(WorkList);

    const filterItem = (categItem) => {
        const updatedItems = WorkList.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }
    return (
        <div className="container">

            <div className="menu-items">
                <div className="row">
                    <div className="col-11 item">
                        <div className="row">
                            {
                                items.map((elem) => {
                                    const { name, image, description, tag } = elem;

                                    return (
                                        <div className="item">
                                            <div className="item1 col-12">
                                                <div className="row item-inside">
                                                    <div className="col-12 img-div">
                                                        <img src={image} alt={name} className="img" />
                                                        <div className="item-content">
                                                            <h1>{name}</h1>
                                                            <p>{description}</p>
                                                            <div className="tag">
                                                                <span className="badge-light">{tag}</span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Works;
