import React, {useState} from 'react'
import "../styles/tab.css";
import MenuList from "./MenuList";


function Works() {
    const [items, setItems] = useState(MenuList);

    const filterItem = (categItem) => {
        const updatedItems = MenuList.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }
    return (
        <div className="container">
            <div className="works-header">
                <h1>Works</h1>
                <div className="menu-tabs container">
                    <div className="menu-tab">
                        <button className="btn" onClick={() => setItems(MenuList)}>All</button>
                        <button className="btn" onClick={() => filterItem('designs')}>Designs</button>
                        <button className="btn" onClick={() => filterItem('website')}>Website</button>
                    </div>
                </div>

                {/* my main items section  */}
                <div className="menu-items container-fluid mt-5">
                    <div className="row">
                        <div className="col-11 mx-auto">
                            <div className="row my-5">
                                {
                                    items.map((elem) => {
                                        const { id, name, image, description } = elem;

                                        return (
                                            <div className="item1 col-12 col-md-6 col-lg-6 col-xl-4 my-5">
                                                <div className="row Item-inside">
                                                    <div className="col-12 col-md-12 col-lg-4 img-div">
                                                        <img src={image} alt={name} className="img-fluid"/>
                                                    </div>
                                                    <div className="col-12 col-md-12 col-lg-8">
                                                        <div className="main-title pt-4 pb-3">
                                                            <h1>{name}</h1>
                                                            <p>{description }</p>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                            }


                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Works;
